CREATE OR REPLACE FUNCTION obter_valor_projeto ( nr_sequencia_p bigint, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE



vl_projeto_vigencia_w		double precision;
vl_projeto_w			double precision;
vl_retorno_w			double precision;
qt_existe_w			integer;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	projeto_recurso_vigencia c,
	projeto_recurso b
where 	b.nr_sequencia = c.nr_seq_projeto
and	b.nr_sequencia = nr_sequencia_p;

if (qt_existe_w > 0) then
	begin
	select	coalesce(max(c.vl_projeto),0)
	into STRICT	vl_projeto_vigencia_w
	from	projeto_recurso_vigencia c
	where	((coalesce(dt_inicio_exec::text, '') = '') or (trunc(dt_inicio_exec,'dd') <= trunc(dt_mesano_referencia_p,'dd')))
	and	((coalesce(dt_fim_exec::text, '') = '') or (trunc(dt_fim_exec,'dd') >= trunc(dt_mesano_referencia_p,'dd')))
	and	nr_seq_projeto = nr_sequencia_p;

	vl_retorno_w	:= vl_projeto_vigencia_w;
	end;
else
	begin
	select	coalesce(max(b.vl_projeto),0)
	into STRICT	vl_projeto_w
	from 	projeto_recurso b
	where 	b.nr_sequencia = nr_sequencia_p;

	vl_retorno_w	:= vl_projeto_w;
	end;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
