CREATE OR REPLACE FUNCTION obter_valor_rateio_cotacao ( nr_cot_compra_p bigint) RETURNS bigint AS $body$
DECLARE


vl_valor_w		double precision;


BEGIN

select	sum(vl_total)
into STRICT	vl_valor_w
from (
	SELECT	sum(min(coalesce(i.vl_unitario_material,0)) * min(coalesce(i.qt_material,0))) vl_total
	from	cot_compra_forn_item i,
		cot_compra_forn f
	where	f.nr_sequencia		= i.nr_seq_cot_forn
	and	f.nr_cot_compra		= nr_cot_compra_p
	and	coalesce(i.vl_unitario_material,0)	> 0
	and	coalesce(i.qt_material,0)		> 0
	group by i.nr_item_cot_compra) alias8;

Return vl_valor_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
