CREATE OR REPLACE FUNCTION obter_valor_receb_conv_data (nr_sequencia_receb_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		bigint;
vl_vinculacao_w		bigint;
vl_vinculado_audit_w	double precision;


BEGIN

select	coalesce(sum(a.vl_vinculacao),0) vl_vinculacao
into STRICT	vl_vinculacao_w
from	convenio_ret_receb a
where	nr_seq_receb	= nr_sequencia_receb_p
and	dt_atualizacao	> dt_referencia_p;

select	coalesce(sum(a.vl_vinculado),0) vl_vinculacao
into STRICT	vl_vinculado_audit_w
from	lote_audit_hist_receb a
where	nr_seq_receb	= nr_sequencia_receb_p
and	dt_atualizacao	> dt_referencia_p;


vl_retorno_w	:= vl_vinculacao_w - vl_vinculado_audit_w;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
