CREATE OR REPLACE FUNCTION obter_valor_regulacao ( nm_tabela_p text, nm_atributo_p text, cd_interno_p text) RETURNS varchar AS $body$
DECLARE


cd_retorno_w	conversao_meio_externo.cd_externo%type;


BEGIN

select	max(cd_externo)
into STRICT	cd_retorno_w
from	conversao_meio_externo
where	ie_sistema_externo = 'REG'
and	upper(nm_tabela) = upper(nm_tabela_p)
and	upper(nm_atributo) = upper(nm_atributo_p)
and	upper(cd_interno) = upper(cd_interno_p);

return	cd_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
