CREATE OR REPLACE FUNCTION obter_valor_rendimentos_mes (dt_referencia_p timestamp, qt_mes_p text, cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, cd_receita_p text) RETURNS bigint AS $body$
DECLARE

vl_titulo_w		double precision;
dt_liquidacao_w		timestamp;


BEGIN

dt_liquidacao_w		:= to_date('01/'||qt_mes_p||'/'||to_char(dt_referencia_p,'yyyy'));

if (cd_receita_p in ('1708','8045','3280')) then

	if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
		select	coalesce(sum(CASE WHEN i.ie_origem_titulo=2 THEN  i.vl_titulo  ELSE CASE WHEN coalesce(i.nr_documento::text, '') = '' THEN  i.vl_titulo  ELSE OBTER_VALORES_NF(i.nr_seq_nota_fiscal, '1') END  END ),0)
		into STRICT	vl_titulo_w
		from	titulo_pagar i
		where	trunc(i.dt_contabil,'month') = trunc(dt_liquidacao_w,'month')
		and	i.cd_estabelecimento = cd_estabelecimento_p
		and	i.cd_cgc = cd_cgc_p
		and	ie_situacao <> 'C'
		and	ie_tipo_titulo in ('0','11','13');
	end if;
else
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select	coalesce(sum(CASE WHEN i.ie_origem_titulo=2 THEN  i.vl_titulo  ELSE CASE WHEN coalesce(i.nr_documento::text, '') = '' THEN  i.vl_titulo  ELSE OBTER_VALORES_NF(i.nr_seq_nota_fiscal, '1') END  END ),0)
		into STRICT	vl_titulo_w
		from	titulo_pagar i
		where	trunc(i.dt_liquidacao,'month') = trunc(dt_liquidacao_w,'month')
		and	i.cd_estabelecimento = cd_estabelecimento_p
		and	i.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	ie_situacao <> 'C'
		and	ie_tipo_titulo in ('0','11','13');
	end if;
	if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
		select	coalesce(sum(CASE WHEN i.ie_origem_titulo=2 THEN  i.vl_titulo  ELSE CASE WHEN coalesce(i.nr_documento::text, '') = '' THEN  i.vl_titulo  ELSE OBTER_VALORES_NF(i.nr_seq_nota_fiscal, '1') END  END ),0)
		into STRICT	vl_titulo_w
		from	titulo_pagar i
		where	trunc(i.dt_liquidacao,'month') = trunc(dt_liquidacao_w,'month')
		and	i.cd_estabelecimento = cd_estabelecimento_p
		and	i.cd_cgc = cd_cgc_p
		and	ie_situacao <> 'C'
		and	ie_tipo_titulo in ('0','11','13');
	end if;
end if;

return vl_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
