CREATE OR REPLACE FUNCTION obter_valor_repasse_cheque (nr_seq_proc_repasse_p bigint, nr_seq_mat_repasse_p bigint, nr_seq_cheque_p bigint) RETURNS bigint AS $body$
DECLARE


-- Function para retornar o valor de repasse do item proporcional ao valor do cheque
nr_interno_conta_w		bigint;
vl_item_repasse_w		double precision;
vl_tot_proc_repasse_w		double precision;
vl_total_repasse_w		double precision;
vl_cheque_w			double precision;
vl_proporcional_w		double precision;


BEGIN

if (coalesce(nr_seq_proc_repasse_p,0) > 0) then

	select	b.nr_interno_conta
	into STRICT	nr_interno_conta_w
	from	procedimento_paciente b,
		procedimento_repasse a
	where	a.nr_seq_procedimento	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_proc_repasse_p;

	select	vl_repasse
	into STRICT	vl_item_repasse_w
	from	procedimento_repasse
	where	nr_sequencia		= nr_seq_proc_repasse_p;
else
	select	b.nr_interno_conta
	into STRICT	nr_interno_conta_w
	from	material_atend_paciente b,
		material_repasse a
	where	a.nr_seq_material	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_mat_repasse_p;

	select	vl_repasse
	into STRICT	vl_item_repasse_w
	from	material_repasse
	where	nr_sequencia		= nr_seq_mat_repasse_p;

end if;

select	coalesce(sum(b.vl_repasse),0)
into STRICT	vl_tot_proc_repasse_w
from	procedimento_repasse b,
	procedimento_paciente a
where	a.nr_sequencia		= b.nr_seq_procedimento
and	a.nr_interno_conta	= nr_interno_conta_w;

select	coalesce(sum(b.vl_repasse),0) + vl_tot_proc_repasse_w
into STRICT	vl_total_repasse_w
from	material_repasse b,
	material_atend_paciente a
where	a.nr_sequencia		= b.nr_seq_material
and	a.nr_interno_conta	= nr_interno_conta_w;

select	vl_cheque
into STRICT	vl_cheque_w
from	cheque_cr
where	nr_seq_cheque	= nr_seq_cheque_p;


vl_proporcional_w		:= dividir_sem_round(vl_item_repasse_w, vl_total_repasse_w) * vl_cheque_w;

return	vl_proporcional_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_repasse_cheque (nr_seq_proc_repasse_p bigint, nr_seq_mat_repasse_p bigint, nr_seq_cheque_p bigint) FROM PUBLIC;

