SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_repasse_conta ( nr_interno_conta_p bigint, cd_autorizacao_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_repasse_w		double precision;

vl_repasse_proc_w		double precision;
vl_Liberado_Proc_w		double precision;
vl_pendente_Proc_w		double precision;

vl_repasse_mat_w		double precision;
vl_Liberado_mat_w		double precision;
vl_pendente_mat_w		double precision;

/* Opção
R - Repasse
L - Liberado
P - Pendente
*/
BEGIN
select	sum(x.vl_repasse),
	sum(x.vl_liberado)
into STRICT	vl_repasse_proc_w,
	vl_liberado_proc_w
from (
	SELECT
		coalesce(sum(vl_repasse),0) vl_repasse,
		coalesce(sum(vl_liberado),0) vl_liberado,
		coalesce(sum(vl_repasse),0) - coalesce(sum(vl_liberado),0) vl_pendente
	from 	procedimento_repasse b,
		procedimento_paciente a
	where	b.nr_seq_procedimento	= a.nr_sequencia
	  and	a.nr_interno_conta		= nr_interno_conta_p
	  and	coalesce(b.nr_seq_partic::text, '') = ''
	  and (a.nr_doc_convenio		= cd_autorizacao_p or ('0' = coalesce(cd_autorizacao_p,'0')))
	
union	all

	  PERFORM
		coalesce(sum(vl_repasse),0),
		coalesce(sum(vl_liberado),0),
		coalesce(sum(vl_repasse),0) - coalesce(sum(vl_liberado),0)
	from 	procedimento_participante c,
		procedimento_repasse b,
		procedimento_paciente a
	where	b.nr_seq_procedimento	= a.nr_sequencia
	  and	a.nr_interno_conta		= nr_interno_conta_p
	  and	b.nr_seq_partic			= c.nr_seq_partic
	  and	c.nr_sequencia			= a.nr_sequencia
	  and (a.nr_doc_convenio		= cd_autorizacao_p or ('0' = coalesce(cd_autorizacao_p,'0')))) x;

select	sum(x.vl_pendente)
into STRICT	vl_pendente_proc_w
from (
	SELECT	coalesce(sum(vl_repasse),0) - coalesce(sum(vl_liberado),0) vl_pendente
	from 	procedimento_repasse b,
		procedimento_paciente a
	where	b.nr_seq_procedimento	= a.nr_sequencia
	and	a.nr_interno_conta		= nr_interno_conta_p
	and	coalesce(b.nr_seq_partic::text, '') = ''
	and (a.nr_doc_convenio		= cd_autorizacao_p or ('0' = coalesce(cd_autorizacao_p,'0')))
	and	coalesce(b.nr_repasse_terceiro::text, '') = ''
	and	b.ie_status			not in ('G','P')
	
union	all

	PERFORM	coalesce(sum(vl_repasse),0) - coalesce(sum(vl_liberado),0)
	from 	procedimento_participante c,
		procedimento_repasse b,
		procedimento_paciente a
	where	b.nr_seq_procedimento	= a.nr_sequencia
	and	a.nr_interno_conta		= nr_interno_conta_p
	and	b.nr_seq_partic			= c.nr_seq_partic
	and	c.nr_sequencia			= a.nr_sequencia
	and (a.nr_doc_convenio		= cd_autorizacao_p or ('0' = coalesce(cd_autorizacao_p,'0')))
	and	coalesce(b.nr_repasse_terceiro::text, '') = ''
	and	b.ie_status			not in ('G','P')) x;

select
	coalesce(sum(vl_repasse),0),
	coalesce(sum(vl_liberado),0)
into STRICT	vl_repasse_mat_w,
	vl_liberado_mat_w
from 	material_repasse b,
	material_atend_paciente a
where	b.nr_seq_material	= a.nr_sequencia
  and	a.nr_interno_conta	= nr_interno_conta_p
  and (a.nr_doc_convenio		= cd_autorizacao_p or ('0' = coalesce(cd_autorizacao_p,'0')));

select	coalesce(sum(vl_repasse),0) - coalesce(sum(vl_liberado),0)
into STRICT	vl_pendente_mat_w
from 	material_repasse b,
	material_atend_paciente a
where	b.nr_seq_material	= a.nr_sequencia
and	a.nr_interno_conta	= nr_interno_conta_p
and (a.nr_doc_convenio	= cd_autorizacao_p or ('0' = coalesce(cd_autorizacao_p,'0')))
and	coalesce(b.nr_repasse_terceiro::text, '') = ''
and	b.ie_status		not in ('G','P');

if (ie_opcao_p	= 'R') then
	vl_repasse_w	:= vl_repasse_proc_w + vl_repasse_mat_w;
elsif (ie_opcao_p	= 'P') then
	vl_repasse_w	:= vl_pendente_proc_w + vl_pendente_mat_w;
else
	vl_repasse_w	:= vl_liberado_proc_w + vl_liberado_mat_w;
end if;

RETURN vl_repasse_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_repasse_conta ( nr_interno_conta_p bigint, cd_autorizacao_p text, ie_opcao_p text) FROM PUBLIC;

