CREATE OR REPLACE FUNCTION obter_valor_repasse_item_rep (nr_seq_nota_p bigint, nr_seq_item_p bigint) RETURNS bigint AS $body$
DECLARE


vl_repasse_w	double precision;


BEGIN

select	coalesce(sum(vl_repasse),0)
into STRICT	vl_repasse_w
from	nota_fiscal_item_rep r
where	r.nr_seq_nf 		= nr_seq_nota_p
and	r.nr_seq_item_nf	= nr_seq_item_p;

return	vl_repasse_w;

end;
