CREATE OR REPLACE FUNCTION obter_valor_reserva_fatura ( nr_seq_fatura_p bigint ) RETURNS bigint AS $body$
DECLARE


vl_reserva_w	double precision;


BEGIN

select	sum(coalesce(vl_reserva,0))
into STRICT	vl_reserva_w
from	via_fat_reserva
where	nr_seq_fatura	=	nr_seq_fatura_p;

return	vl_reserva_w;

end;
