CREATE OR REPLACE FUNCTION obter_valor_reserva_viagem ( nr_seq_viagem_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_w		double precision:= 0;


BEGIN

select	coalesce(sum(VL_RESERVA),0) vl_total
into STRICT	vl_total_w
from	via_reserva a
where	a.nr_seq_viagem	= nr_seq_viagem_p
and		a.ie_responsavel_custo <> 'N'
and		coalesce(ie_cancelamento::text, '') = ''
and		a.ie_tipo_reserva	= 'T'
having	coalesce(sum(VL_RESERVA),0) > 0;

return vl_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
