CREATE OR REPLACE FUNCTION obter_valor_sadt ( nr_seq_proc_p bigint) RETURNS bigint AS $body$
DECLARE

			 
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
dt_procedimento_w	timestamp;
vl_materiais_w		double precision	:= 0;
vl_sadt_w		double precision	:= 0;
pr_sadt_w		double precision	:= 0;
qt_procedimento_w	double precision	:= 0;
vl_sh_unif_w		double precision	:= 0;
cd_estabelecimento_w	smallint;
cd_convenio_w		integer;


BEGIN 
 
select	a.cd_procedimento, 
	a.ie_origem_proced, 
	a.dt_procedimento, 
	a.vl_materiais, 
	a.qt_procedimento, 
	c.cd_convenio_parametro 
into STRICT	cd_procedimento_w, 
	ie_origem_proced_w, 
	dt_procedimento_w, 
	vl_materiais_w, 
	qt_procedimento_w, 
	cd_convenio_w 
from	procedimento_paciente a, 
	conta_paciente c 
where	c.nr_interno_conta	= a.nr_interno_conta 
and	a.nr_sequencia		= nr_seq_proc_p;
 
begin 
select	coalesce(vl_sadt,0) 
into STRICT	vl_sadt_w 
from	convenio_sadt 
where	cd_procedimento		= cd_procedimento_w 
and	ie_origem_proced	= ie_origem_proced_w 
and	cd_convenio		= cd_convenio_w 
and	dt_procedimento_w between dt_inicio_vigencia and coalesce(dt_final_vigencia,dt_procedimento_w);
exception 
	when no_data_found then 
		vl_sadt_w	:= 0;
	when others then 
		/*R.aise_application_error(-20011,'Problema no cadastro de SADT para o procedimento ' || cd_procedimento_w || '.' || chr(13) || 
					'Não pode existir registro duplicado para o mesmo procedimento dentro de uma mesma competência #@#@');*/
 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263419,'cd_procedimento_w='||cd_procedimento_w);
end;
 
return(vl_sadt_w * qt_procedimento_w);
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
