SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_san_parametro ( ie_tipo_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE



-- RH - Fator RH
-- TS - Tipagem Sanguinea
nr_seq_exame_rh_w      	bigint;
nr_seq_exame_tipo_w 	bigint;
ds_retorno_w		varchar(255);
ie_utiliza_fisica_clinica_w	san_parametro.ie_utiliza_fisica_clinica%type;


BEGIN

select 	max(nr_seq_exame_rh),
	max(nr_seq_exame_tipo),
	max(ie_utiliza_fisica_clinica)
into STRICT	nr_seq_exame_rh_w,
	nr_seq_exame_tipo_w,
		ie_utiliza_fisica_clinica_w
from 	san_parametro
where	cd_estabelecimento = cd_estabelecimento_p;

if ( ie_tipo_p = 'RH') then
	ds_retorno_w := nr_seq_exame_rh_w;
elsif ( ie_tipo_p = 'TS') then
	ds_retorno_w := nr_seq_exame_tipo_w;
elsif (ie_tipo_p = 'TC') then
	ds_retorno_w := ie_utiliza_fisica_clinica_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
