CREATE OR REPLACE FUNCTION obter_valor_sem_virgula (vl_item_p bigint) RETURNS bigint AS $body$
DECLARE


i			bigint;
caracter_w		varchar(15);
vl_resultado_w		double precision;
decimais_w		smallint;
vl_aux_w		varchar(15);
ie_conta_decimais_w	varchar(2);


BEGIN

ie_conta_decimais_w:= 'N';
decimais_w:= 0;
vl_aux_w:= '';

for i in 1..length(vl_item_p) loop
	begin
        caracter_w:= Substr(vl_item_p, i, 1);
	if (caracter_w = ',') then
		vl_aux_w := Substr(vl_item_p, 1, i-1);
		ie_conta_decimais_w:= 'S';
	end if;
	if  (ie_conta_decimais_w = 'S' AND caracter_w <> ',') then
		vl_aux_w:= vl_aux_w || caracter_w;
		decimais_w  := decimais_w + 1;
        end if;
	end;
END loop;


if decimais_w = 0 then
	vl_aux_w:= vl_item_p || '00';
elsif decimais_w = 1 then
	vl_aux_w:= vl_aux_w || '0';
end if;

vl_resultado_w:= (vl_aux_w)::numeric;

RETURN vl_resultado_w;
END;
$body$
LANGUAGE PLPGSQL
