CREATE OR REPLACE FUNCTION obter_valor_sus_exames_real (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno				double precision;
vl_procedimento_retorno		bigint;


BEGIN

select (A.VL_DIARIA + A.VL_TAXAS +A.VL_ATO_ANESTESISTA+ A.VL_SADT + A.VL_TPH + A.VL_DIALISE + A.VL_SADT_RX + A.VL_SADT_PC + A.VL_MATMED + 	A.VL_CONTRASTE + A.VL_FILME_RX + A.VL_GESSO + A.VL_QUIMIOTERAPIA + A.VL_FILME_RESSONANCIA + A.VL_OUTROS + 		A.VL_MEDICO) VL_TOTAL_SUS,
	b.vl_procedimento
into STRICT	vl_retorno,
	vl_procedimento_retorno
from	SUS_VALOR_PROC_PACIENTE A,
	procedimento_paciente b
where	a.nr_sequencia = b.nr_sequencia
and	b.nr_sequencia = nr_sequencia_p;

if (vl_retorno = 0) then
	vl_retorno := vl_procedimento_retorno;
end if;

return vl_retorno;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
