SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_taxa (nr_seq_taxa_p bigint, ie_tipo_valor_p bigint) RETURNS bigint AS $body$
DECLARE


vl_taxa_w	double precision;


BEGIN

select  coalesce(sum(a.vl_ajuste),0)
into STRICT	vl_taxa_w
from    TAXA_RESUMO_CONTA a, material_atend_paciente b
where   a.nr_seq_servico                = nr_seq_taxa_p
and 	a.NR_SEQ_MAT_PROC = b.nr_sequencia
and 	coalesce(b.nr_seq_proc_pacote::text, '') = '';


return	vl_taxa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
