CREATE OR REPLACE FUNCTION obter_valor_terc_cobranca ( nr_seq_cobranca_p bigint) RETURNS bigint AS $body$
DECLARE


vl_terceiro_w	double precision;


BEGIN
select	coalesce(sum(vl_terceiro),0)
