SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_tipo_trans_financ (nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_transacao_w		double precision;


BEGIN

if (ie_opcao_p	= 'C') then
	select	coalesce(sum(d.vl_cheque),0)
	into STRICT	vl_transacao_w
	from	movto_trans_financ a,
		cheque_cr d
	where	a.nr_Seq_cheque		= d.nr_seq_cheque
	and	a.nr_seq_saldo_caixa	= nr_sequencia_p
	and	(a.nr_seq_cheque IS NOT NULL AND a.nr_seq_cheque::text <> '')
	and	coalesce(d.dt_compensacao::text, '') = '';
end if;

if (ie_opcao_p	= 'E') then
	select	coalesce(sum(a.vl_transacao),0)
	into STRICT	vl_transacao_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	a.nr_seq_trans_financ	= b.nr_sequencia
	  and	a.nr_seq_saldo_caixa	= nr_sequencia_p
	  and	b.ie_saldo_caixa	= 'E';
end if;

if (ie_opcao_p	= 'S') then
	select	coalesce(sum(a.vl_transacao),0)
	into STRICT	vl_transacao_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	a.nr_seq_trans_financ	= b.nr_sequencia
	  and	a.nr_seq_saldo_caixa	= nr_sequencia_p
	  and	b.ie_saldo_caixa	= 'S'
	  and	b.ie_saldo_caixa		<> 'D';
end if;

return	vl_transacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
