CREATE OR REPLACE FUNCTION obter_valor_tipo_trib_item_nf ( nr_sequencia_p bigint, nr_item_nf_p bigint, ie_tipo_tributo_p text) RETURNS bigint AS $body$
DECLARE


vl_ipi_w		double precision := 0;


BEGIN
select	coalesce(sum(n.vl_tributo),0)
into STRICT	vl_ipi_w
from	tributo t,
	nota_fiscal_item_trib n
where	t.cd_tributo		= n.cd_tributo
and	t.ie_tipo_tributo		= ie_tipo_tributo_p
and	n.nr_sequencia		= nr_sequencia_p
and	n.nr_item_nf		= coalesce(nr_item_nf_p,n.nr_item_nf);

return vl_ipi_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
