CREATE OR REPLACE FUNCTION obter_valor_titulo_retorno ( nr_seq_retorno_p bigint, nr_titulo_p bigint, ie_opcao_p bigint default 0) RETURNS bigint AS $body$
DECLARE


/*IE_OPCAO
1 - valor dos impostos do titulo
2 - valor da porcentagem dos impostos sobre o titulo
3 - Calcular valor da porcentagem do titulo passado por parametro
*/
vl_imposto_w	convenio_receb_adic.vl_adicional%type;
ds_retorno_w	convenio_receb_adic.vl_adicional%type := 0;
vl_titulo_w	titulo_receber.vl_titulo%type;


BEGIN

select	vl_titulo
into STRICT	vl_titulo_w
from	titulo_receber
where	nr_titulo = nr_titulo_p;

select	coalesce(sum(a.vl_adicional),0)
into STRICT	vl_imposto_w
from	convenio_receb_adic a,
	convenio_receb b,
	convenio_ret_receb c
where	a.nr_seq_Receb		= b.nr_sequencia
and	c.nr_seq_receb		= b.nr_sequencia
and	c.nr_seq_retorno	= nr_seq_retorno_p;

if (coalesce(ie_opcao_p,0) = 1) then
	ds_retorno_w	:= vl_imposto_w;
end if;

if (coalesce(ie_opcao_p,0) = 2) then
	ds_retorno_w := (vl_imposto_w * 100) / vl_titulo_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
