CREATE OR REPLACE FUNCTION obter_valor_tit_cob_fin ( nr_sequencia_p bigint, nr_seq_cliente_p bigint) RETURNS bigint AS $body$
DECLARE


vl_saldo_w	double precision;


BEGIN
select	sum(coalesce(b.vl_saldo_titulo,0))
into STRICT	vl_saldo_w
from	fin_titulo_lote_clie a,
	titulo_receber b
where	b.nr_titulo = a.nr_titulo
and	a.nr_seq_cliente = nr_seq_cliente_p
and	a.nr_lote_cliente = nr_sequencia_p;

return	vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
