SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_tit_rec_desdobrado (nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


vl_titulo_w		double precision;
vl_desdobrado_w		double precision;
vl_retorno_w		double precision;


BEGIN

select	vl_titulo
into STRICT	vl_titulo_w
from	titulo_receber
where	nr_titulo	= nr_titulo_p;

select	coalesce(sum(vl_titulo),0)
into STRICT	vl_desdobrado_w
from	titulo_receber_desdob
where	(nr_titulo_dest IS NOT NULL AND nr_titulo_dest::text <> '')
and	nr_titulo = nr_titulo_p;

vl_retorno_w	:= vl_titulo_w - vl_desdobrado_w;

return	coalesce(vl_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_tit_rec_desdobrado (nr_titulo_p bigint) FROM PUBLIC;

