CREATE OR REPLACE FUNCTION obter_valor_tit_rec_retorno (nr_interno_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_pago_w			bigint;
vl_glosado_w			bigint;
vl_adicional_w			bigint;
vl_retorno_w			bigint;


BEGIN

select	coalesce(sum(vl_pago),0) vl_pago,
	coalesce(sum(vl_glosado),0) vl_glosado,
	coalesce(sum(vl_adicional),0) vl_adicional
into STRICT	vl_pago_w,
	vl_glosado_w,
	vl_adicional_w
from	convenio_retorno_item
where	nr_interno_conta	= nr_interno_conta_p;

if (ie_opcao_p = 'P') then
	vl_retorno_w := vl_pago_w;
elsif (ie_opcao_p =  'G') then
	vl_retorno_w := vl_glosado_w;
elsif (ie_opcao_p = 'A') then
	vl_retorno_w := vl_adicional_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
