SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_tit_transf (nr_titulo_p bigint, dt_baixa_p timestamp, ie_tipo_tit text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
vl_titulo_w		double precision;
vl_alteracao_w		double precision;
nr_seq_alteracao_w	varchar(5);
dt_baixa_w		timestamp;


BEGIN

if (ie_tipo_tit = 'CR') then

	select	a.vl_titulo
	into STRICT	vl_titulo_w
	from	titulo_receber a
	where	a.nr_titulo	= nr_titulo_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_alteracao_w
	from	alteracao_valor
	where	nr_titulo	= nr_titulo_p;

	dt_baixa_w	:= fim_dia(dt_baixa_p);

	select	coalesce(sum(CASE WHEN a.ie_aumenta_diminui='A' THEN  a.vl_alteracao  ELSE a.vl_alteracao * -1 END ),0)
	into STRICT	vl_alteracao_w
	from	alteracao_valor a
	where	a.nr_titulo	= nr_titulo_p
	and	a.nr_sequencia 	<> nr_seq_alteracao_w
	and	a.dt_alteracao	<= dt_baixa_w;

elsif (ie_tipo_tit = 'CP') then

	select	a.vl_titulo
	into STRICT	vl_titulo_w
	from	titulo_pagar a
	where	a.nr_titulo	= nr_titulo_p;

	dt_baixa_w	:= fim_dia(dt_baixa_p);

	SELECT	coalesce(SUM(vl_anterior - vl_alteracao),0)
	INTO STRICT	vl_alteracao_w
	FROM	titulo_pagar_alt_valor
	WHERE	dt_alteracao	<= dt_baixa_w
	and	nr_titulo	= nr_titulo_p;

end if;

ds_retorno_w	:= vl_titulo_w + vl_alteracao_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
