CREATE OR REPLACE FUNCTION obter_valor_total_hotel ( nr_seq_reserva_p bigint) RETURNS bigint AS $body$
DECLARE


vl_total_hotel_w		double precision:= 0;
ie_tipo_reserva_w		varchar(1);
qt_dias_hospedagem_w	integer:= 0;
nr_seq_transporte_w	bigint;
vl_diaria_hotel_w		double precision:= 0;
qt_valor_adic_w			double precision:= 0;


BEGIN

begin
select	ie_tipo_reserva
into STRICT	ie_tipo_reserva_w
from	via_reserva
where	nr_sequencia = nr_seq_reserva_p
and		coalesce(ie_cancelamento::text, '') = '';

select	nr_seq_transporte
into STRICT	nr_seq_transporte_w
from 	via_reserva_transporte
where	nr_seq_reserva = nr_seq_reserva_p;
exception
	when others then
		nr_seq_transporte_w:= 0;
end;

begin
select 	coalesce(qt_dias_hospedagem,0)
into STRICT	qt_dias_hospedagem_w
from 	via_transporte
where 	nr_sequencia = nr_seq_transporte_w;
exception
	when others then
		qt_dias_hospedagem_w:= 0;
end;

begin
select 	max(coalesce(vl_reserva,0))
into STRICT	vl_diaria_hotel_w
from 	via_reserva
where 	nr_sequencia = nr_seq_reserva_p
and		coalesce(ie_cancelamento::text, '') = '';
exception
	when others then
		vl_diaria_hotel_w:= 0;
end;

if (ie_tipo_reserva_w = 'H') then

	if (qt_dias_hospedagem_w = 0) then
		vl_total_hotel_w:=	vl_diaria_hotel_w;
	else
		vl_total_hotel_w:=	vl_diaria_hotel_w * qt_dias_hospedagem_w;
	end if;

elsif (ie_tipo_reserva_w = 'T') then

	select	max(vl_reserva)
	into STRICT	vl_total_hotel_w
	from	via_reserva
	where	nr_sequencia = nr_seq_reserva_p
	and		coalesce(ie_cancelamento::text, '') = '';


end if;

return vl_total_hotel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
