CREATE OR REPLACE FUNCTION obter_valor_total_mat_autor (vl_unitario_p bigint, vl_cotado_p bigint, ie_origem_preco_p bigint, ie_reducao_acrescimo_p text, qt_solicitada_p bigint, pr_adicional_p bigint) RETURNS bigint AS $body$
DECLARE

 
			 
vl_retorno_w			double precision;	
ie_regra_acresc_mat_autor_w	varchar(1) := 'N';
cd_estabelecimento_w		integer := wheb_usuario_pck.Get_cd_estabelecimento;	
			 

BEGIN 
 
select	coalesce(max(IE_REGRA_ACRESC_MAT_AUTOR),'N') 
into STRICT	ie_regra_acresc_mat_autor_w 
from	parametro_faturamento 
where	cd_estabelecimento = cd_estabelecimento_w;
 
if (coalesce(ie_regra_acresc_mat_autor_w,'N') = 'S') then 
	 
	 
	vl_retorno_w := (coalesce(vl_unitario_p,0) * coalesce(qt_solicitada_p,0));
 
else 
	select	coalesce(CASE WHEN ie_reducao_acrescimo_p='R' THEN (coalesce(vl_unitario_p,0) * coalesce(qt_solicitada_p,0)) - 		((coalesce(vl_unitario_p,0) * coalesce(qt_solicitada_p,0)) * coalesce(pr_adicional_p/100,0))  ELSE (coalesce(vl_unitario_p,0) * coalesce(qt_solicitada_p,0)) + 		((coalesce(vl_unitario_p,0) * coalesce(qt_solicitada_p,0)) * coalesce(pr_adicional_p/100,0)) END ,0) 
	into STRICT	vl_retorno_w 
	;
	 
end if;
 
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
