CREATE OR REPLACE FUNCTION obter_valor_total_modulo_impl ( nr_seq_cliente_p bigint, ie_listar_modulo_p text) RETURNS bigint AS $body$
DECLARE


vl_total_w	double precision;


BEGIN
if (nr_seq_cliente_p IS NOT NULL AND nr_seq_cliente_p::text <> '') then
	begin

	if (ie_listar_modulo_p IS NOT NULL AND ie_listar_modulo_p::text <> '') and (ie_listar_modulo_p <> 'T') then
		begin

		select	coalesce(avg(coalesce(a.pr_utilizacao,0)),0)
		into STRICT	vl_total_w
		from 	com_cliente_mod_impl a
		where 	a.nr_seq_cliente = nr_seq_cliente_p
		and	exists (SELECT 1
				from	modulo_implantacao x
				where	a.nr_seq_modulo = x.nr_sequencia
				and	coalesce(x.ie_gestao, 'P') = ie_listar_modulo_p);
		end;
	else
		begin

		select	coalesce(avg(coalesce(a.pr_utilizacao,0)),0) vl_total_modulo
		into STRICT	vl_total_w
		from 	com_cliente_mod_impl a
		where 	a.nr_seq_cliente = nr_seq_cliente_p;
		end;
	end if;
	end;
end if;
return vl_total_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
