SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_total_rat ( nr_rat_p bigint, cd_consultor_p bigint) RETURNS bigint AS $body$
DECLARE


qt_min_w	double precision := 0;
vl_total_w	double precision := 0;


BEGIN

select	sum(qt_min_ativ)
into STRICT	qt_min_w
from	proj_rat_ativ
where	nr_seq_rat = nr_rat_p;

select	sum(dividir(qt_min_w, 60) * a.vl_hora)
into STRICT	vl_total_w
from	PROJ_CONSULTOR_AVAL a,
	com_cliente b,
	proj_rat c
where	a.nr_seq_cliente	= b.nr_sequencia
and	a.cd_consultor 	= cd_consultor_p
and	c.nr_seq_cliente 	= b.nr_sequencia
and	c.nr_sequencia 	= nr_rat_p;

return vl_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
