SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_trans_financ (nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_transacao_w		double precision;


BEGIN

if (ie_opcao_p	= 'D') then
	select	coalesce(a.vl_transacao,0)
	into STRICT	vl_transacao_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	a.nr_seq_trans_financ	= b.nr_sequencia
	  and	a.nr_sequencia		= nr_sequencia_p
	  and	b.ie_caixa		in ('D', 'A', 'T');
end if;

if (ie_opcao_p	= 'E') then
	select	coalesce(a.vl_transacao,0)
	into STRICT	vl_transacao_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	a.nr_seq_trans_financ	= b.nr_sequencia
	  and	a.nr_sequencia		= nr_sequencia_p
	  and	b.ie_saldo_caixa	= 'E'
	  and	b.ie_caixa		<> 'D';
end if;

if (ie_opcao_p	= 'S') then
	select	coalesce(a.vl_transacao,0)
	into STRICT	vl_transacao_w
	from	transacao_financeira b,
		movto_trans_financ a
	where	a.nr_seq_trans_financ	= b.nr_sequencia
	  and	a.nr_sequencia		= nr_sequencia_p
	  and	b.ie_saldo_caixa	= 'S'
	  and	b.ie_caixa		<> 'D';
end if;

return	vl_transacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
