CREATE OR REPLACE FUNCTION obter_valor_tributo_exp_nf (nr_seq_nota_p bigint, ie_tipo_valor_p text, ie_nota_item_p text) RETURNS bigint AS $body$
DECLARE


/*
Nota ou Item:
	N - tabela nota_fiscal_trib
	I  - tabela nota_fiscal_item_trib

Tipos de Valores:

	V - Valor do Tributo
	B - Data base calculo
	X - Taxa de tributo

*/
vl_retorno_w		double precision := 0;
vl_tributo_w		double precision := 0;
vl_base_calculo_w	double precision := 0;
tx_tributo_w		double precision := 0;


BEGIN

if (ie_nota_item_p = 'N') then

	select	coalesce(sum(vl_tributo),0),
		coalesce(sum(vl_base_calculo),0),
		coalesce(sum(tx_tributo),0)
	into STRICT	vl_tributo_w,
		vl_base_calculo_w,
		tx_tributo_w
	from 	nota_fiscal_trib
	where	nr_sequencia	= nr_seq_nota_p
	and	coalesce(ie_exporta_xml,'S') = 'S';

elsif (ie_nota_item_p = 'I') then

	select	coalesce(sum(vl_tributo),0),
		coalesce(sum(vl_base_calculo),0),
		coalesce(sum(tx_tributo),0)
	into STRICT	vl_tributo_w,
		vl_base_calculo_w,
		tx_tributo_w
	from 	nota_fiscal_item_trib
	where	nr_sequencia	= nr_seq_nota_p
	and	coalesce(ie_exporta_xml,'S') = 'S';

end if;



if (ie_tipo_valor_p = 'V') then
	vl_retorno_w	:= vl_tributo_w;
elsif (ie_tipo_valor_p = 'B') then
	vl_retorno_w	:= vl_base_calculo_w;
elsif (ie_tipo_valor_p = 'X') then
	vl_retorno_w	:= tx_tributo_w;
end if;

return	vl_retorno_w	;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
