SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_trib_nota_item ( nr_sequencia_p bigint, nr_item_nf_p bigint, ie_tipo_tributo_p text, ie_tipo_valor_p text, ie_totalizador_p text) RETURNS bigint AS $body$
DECLARE

				
/*
ie_tipo_valor_p
V - valor do tributo
B - base de calculo do tributo
X - taxa do tributo
R - Retencao
VC - valor do tributo com 4 casas deciamais 
*/
			
qt_registros_w		bigint;
vl_taxa_w			double precision;
vl_base_calc_w		double precision;
vl_tributo_w		double precision;
vl_retorno_w		double precision;
ie_retencao_w 	smallint;
vl_tributo_comp_w		double precision;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	nota_fiscal_trib n,
		tributo t
where	n.nr_sequencia = nr_sequencia_p
and		t.cd_tributo = n.cd_tributo
and		((coalesce(ie_tipo_tributo_p,'X') = 'X') or (t.ie_tipo_tributo = ie_tipo_tributo_p))
and		((t.ie_tipo_tributo = 'ISS') or (t.ie_tipo_tributo <> 'ISS' and t.ie_soma_diminui <> 'N'));

if (qt_registros_w > 0) then
	select
		coalesce(sum(n.vl_tributo),0),
		coalesce(sum(n.vl_base_calculo),0),
		coalesce(sum(n.tx_tributo),0),
		max(CASE WHEN n.ie_retencao='N' THEN  2  ELSE 1 END )
	into STRICT
		vl_tributo_w,
		vl_base_calc_w,
		vl_taxa_w,
		ie_retencao_w
	from	
		tributo t,
		nota_fiscal_trib n
	where n.cd_tributo = t.cd_tributo
	and (t.ie_tipo_tributo = ie_tipo_tributo_p or coalesce(ie_tipo_tributo_p,'X') = 'X')
	and	n.nr_sequencia = nr_sequencia_p
	and		((t.ie_tipo_tributo = 'ISS') or (t.ie_tipo_tributo <> 'ISS' and t.ie_soma_diminui <> 'N'));

	
elsif (ie_totalizador_p = 'S') then
	select	
    coalesce(sum((n.vl_base_calculo * n.tx_tributo) / 100), 0),
		coalesce(sum(n.vl_tributo),0),
		coalesce(sum(n.vl_base_calculo),0),
		coalesce(max(n.tx_tributo),0)
	into STRICT
    vl_tributo_comp_w,
		vl_tributo_w,
		vl_base_calc_w,
		vl_taxa_w
	from   	
		tributo t,
        nota_fiscal_item_trib n
	where t.cd_tributo = n.cd_tributo
	and	((coalesce(ie_tipo_tributo_p,'X') = 'X') or (t.ie_tipo_tributo = ie_tipo_tributo_p))
	and n.nr_sequencia = nr_sequencia_p
	and		((t.ie_tipo_tributo = 'ISS') or (t.ie_tipo_tributo <> 'ISS' and t.ie_soma_diminui <> 'N'));
	
else
	select	
		coalesce(sum(n.vl_tributo),0),
		coalesce(sum(n.vl_base_calculo),0),
		coalesce(sum(n.tx_tributo),0)
	into STRICT
		vl_tributo_w,
		vl_base_calc_w,
		vl_taxa_w
	from   	
		tributo t,
        nota_fiscal_item_trib n
	where t.cd_tributo = n.cd_tributo
	and	((coalesce(ie_tipo_tributo_p,'X') = 'X') or (t.ie_tipo_tributo = ie_tipo_tributo_p))
	and n.nr_sequencia = nr_sequencia_p
	and n.nr_item_nf   = nr_item_nf_p
	and		((t.ie_tipo_tributo = 'ISS') or (t.ie_tipo_tributo <> 'ISS' and t.ie_soma_diminui <> 'N'));
end if;

if (ie_tipo_valor_p = 'V') then
	vl_retorno_w	:= vl_tributo_w;
elsif (ie_tipo_valor_p = 'B') then	
	vl_retorno_w	:= vl_base_calc_w;
elsif (ie_tipo_valor_p = 'X') then
	vl_retorno_w	:= vl_taxa_w;
elsif (ie_tipo_valor_p = 'R') then
	vl_retorno_w	:= ie_retencao_w;
elsif (ie_tipo_valor_p = 'VC') then
  vl_retorno_w := vl_tributo_comp_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_trib_nota_item ( nr_sequencia_p bigint, nr_item_nf_p bigint, ie_tipo_tributo_p text, ie_tipo_valor_p text, ie_totalizador_p text) FROM PUBLIC;

