CREATE OR REPLACE FUNCTION obter_valor_trib_repasse (nr_repasse_terceiro_p bigint, ie_tipo_tributo_p text) RETURNS bigint AS $body$
DECLARE


vl_tributo_w	double precision;


BEGIN

select	coalesce(sum(c.vl_imposto), 0)
into STRICT	vl_tributo_w
from	tributo d,
	repasse_terc_venc_trib c,
	repasse_terceiro_venc b,
	repasse_terceiro a
where	d.cd_tributo			= c.cd_tributo
and	d.ie_tipo_tributo		= coalesce(ie_tipo_tributo_p,d.ie_tipo_tributo)
and	c.nr_seq_rep_venc		= b.nr_sequencia
and	b.nr_repasse_terceiro	= a.nr_repasse_terceiro
and	a.nr_repasse_terceiro	= nr_repasse_terceiro_p;

return	vl_tributo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
