CREATE OR REPLACE FUNCTION obter_valor_trib_tit_pagar (nr_titulo_p bigint, ie_tipo_tributo_p text, ie_pago_prev_p text) RETURNS bigint AS $body$
DECLARE


vl_tributo_w	double precision;


BEGIN

select	coalesce(sum(a.vl_imposto),0)
into STRICT	vl_tributo_w
from	tributo b,
	titulo_pagar_imposto a
where	a.cd_tributo		= b.cd_tributo
and	b.ie_tipo_tributo	= ie_tipo_tributo_p
and	a.nr_titulo		= nr_titulo_p
and	a.ie_pago_prev		= coalesce(ie_pago_prev_p, a.ie_pago_prev);

return	vl_tributo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
