CREATE OR REPLACE FUNCTION obter_valor_ultima_compra_data ( cd_estabelecimento_p bigint, qt_dia_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, ie_nota_estoque_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
nr_sequencia_w		bigint;


BEGIN

vl_retorno_w		:= 0;

if (ie_nota_estoque_p = 'N') then
	select
		max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	natureza_operacao o,
		operacao_nota p,
		nota_fiscal b,
		nota_fiscal_item a
	where	a.nr_sequencia		= b.nr_sequencia
	and	b.cd_natureza_operacao	= o.cd_natureza_operacao
	and	o.ie_entrada_saida		= 'E'
	and	b.ie_situacao		= 1
	and	cd_material  		= cd_material_p
	and	b.cd_operacao_nf = p.cd_operacao_nf
	and	coalesce(p.ie_ultima_compra, 'S') = 'S'
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.dt_atualizacao	between(dt_referencia_p - coalesce(qt_dia_p,90)) and dt_referencia_p
	and	((coalesce(cd_local_estoque_p::text, '') = '') or (cd_local_estoque = cd_local_estoque_p))
	and	exists (
		SELECT 1
		from	nota_fiscal b
		where	a.nr_sequencia	= b.nr_sequencia
		and	b.ie_situacao	= '1');
else
	select
		max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	natureza_operacao o,
		operacao_nota p,
		nota_fiscal b,
		nota_fiscal_item a
	where	a.nr_sequencia		= b.nr_sequencia
	and	b.cd_natureza_operacao	= o.cd_natureza_operacao
	and	o.ie_entrada_saida		= 'E'
	and	b.ie_situacao		= 1
	and	cd_material_estoque	= cd_material_p
	and	b.cd_operacao_nf = p.cd_operacao_nf
	and	coalesce(p.ie_ultima_compra, 'S') = 'S'
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	a.dt_atualizacao between(dt_referencia_p - coalesce(qt_dia_p,90)) and dt_referencia_p
	and	((coalesce(cd_local_estoque_p::text, '') = '') or (cd_local_estoque = cd_local_estoque_p))
	and	exists (
		SELECT 1
		from	nota_fiscal b
		where	a.nr_sequencia	= b.nr_sequencia
		and	b.ie_situacao	= '1');
end if;

if (nr_sequencia_w > 0) then
	select	coalesce(max(dividir((a.vl_total_item_nf - a.vl_desconto -
			vl_desconto_rateio + vl_frete + a.vl_despesa_acessoria + a.vl_seguro),
		a.qt_item_estoque)),0)
	into STRICT	vl_retorno_w
	from 	nota_fiscal_item a
	where	nr_sequencia 		= nr_sequencia_w
	and	((ie_nota_estoque_p = 'N' AND cd_material = cd_material_p) or
		(ie_nota_estoque_p <> 'N' AND cd_material_estoque = cd_material_p));
end if;

return	vl_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_ultima_compra_data ( cd_estabelecimento_p bigint, qt_dia_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, ie_nota_estoque_p text, dt_referencia_p timestamp) FROM PUBLIC;

