CREATE OR REPLACE FUNCTION obter_valor_ult_comp_comgen ( cd_material_p bigint, cd_material_gen_p bigint, qt_dia_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_comercial_w	double precision;
vl_generico_w		double precision;
dt_comercial_w	timestamp;
dt_generico_w		timestamp;


BEGIN

select	max(coalesce(obter_valor_ultima_compra(cd_estabelecimento_p,qt_dia_p,cd_material_p,null,'N'),0)),
	max(coalesce(obter_valor_ultima_compra(cd_estabelecimento_p,qt_dia_p,cd_material_gen_p,null,'N'),0))
into STRICT	vl_comercial_w,
	vl_generico_w
;

if	(vl_comercial_w > 0 AND vl_generico_w > 0) then
	begin
	select	obter_data_ultima_compra(cd_material_p, cd_estabelecimento_p),
		obter_data_ultima_compra(cd_material_gen_p, cd_estabelecimento_p)
	into STRICT	dt_comercial_w,
		dt_generico_w
	;

	vl_retorno_w	:=	vl_comercial_w;
	if (dt_generico_w > dt_comercial_w) then
		vl_retorno_w	:=	vl_generico_w;
	end if;
	end;
else
	begin
	if (vl_comercial_w > 0) then
		vl_retorno_w	:=	vl_comercial_w;
	elsif (vl_generico_w > 0) then
		vl_retorno_w	:=	vl_generico_w;
	end if;
	end;
end if;

return	vl_retorno_w;

end;
