CREATE OR REPLACE FUNCTION obter_valor_venc_oc_mes ( nr_ordem_compra_p bigint, dt_mesano_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

 
nr_ordem_compra_w		bigint;
vl_vencimento_w			double precision;	
 

BEGIN 
 
select	coalesce(sum(d.vl_vencimento),0) 
into STRICT	vl_vencimento_w 
from	ordem_compra a, 
	ordem_compra_item b, 
	local_estoque c, 
	ordem_compra_venc d 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_ordem_compra = d.nr_ordem_compra 
and	b.cd_local_estoque = c.cd_local_estoque 
and	a.nr_ordem_compra = nr_ordem_compra_p 
and	c.ie_tipo_local = 8 
and	trunc(d.dt_vencimento,'mm') = trunc(dt_mesano_referencia_p,'mm');
	 
return	vl_vencimento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
