SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_valor_venc_oc (nr_ordem_compra_p bigint, dt_vencimento_p timestamp, cd_conta_financ_p bigint, ie_tratar_fim_semana_p text, dt_final_p timestamp, ie_oc_parcial_p text) RETURNS bigint AS $body$
DECLARE


CD_CONDICAO_PAGAMENTO_w		bigint;
vl_item_liquido_w		double precision;
qt_vencimento_w			smallint;
ds_vencimento_w			varchar(500);
dt_referencia_w			timestamp;
vl_vencimento_w			double precision;
vl_retorno_w			double precision;
vl_total_ordem_w		double precision;
vl_fluxo_w			double precision;
vl_saldo_mercadoria_w		double precision;
vl_venc_ant_w			double precision;
cd_estabelecimento_w		bigint;
ie_calc_fluxo_oc_w		varchar(255);
ie_existe_vencimentos_w		varchar(255);
dt_entrega_w			timestamp;
dt_final_w			timestamp;
pr_desc_financ_w	double precision;
pr_desc_financ_ordem_w	double precision;
dt_orig_referencia_w		timestamp;
IE_ENTREGA_CANC_OC_W	varchar(255);
nr_item_oci_w ordem_compra_item.nr_item_oci%type;


c02 CURSOR FOR
SELECT	b.dt_vencimento,
	dividir_sem_round(c.vl_item_liquido ,obter_valor_liquido_ordem(a.nr_ordem_compra)) * b.vl_vencimento,
	c.pr_desc_financ,
	a.pr_desc_financeiro,
	c.nr_item_oci
from	ordem_compra_item c,
	ORDEM_COMPRA_VENC b,
	ordem_compra a
where	a.nr_ordem_compra	= b.nr_ordem_compra
and	b.nr_ordem_compra	= c.nr_ordem_compra
and	a.nr_ordem_compra	= nr_ordem_compra_p
and	c.nr_seq_conta_financ	= cd_conta_financ_p
AND	b.dt_vencimento		between dt_vencimento_p - 6 and dt_final_w  --  Edgar 02/09/2006 OS 39109, performance
and	ie_calc_fluxo_oc_w	= 'V'
and IE_ENTREGA_CANC_OC_W = 'N'

union

SELECT	
	d.dt_vencimento,
	(c.QT_PREVISTA_ENTREGA * b.VL_UNITARIO_MATERIAL) vl_item,
  b.pr_desc_financ,
  a.pr_desc_financeiro,
  b.nr_item_oci
from 	Ordem_compra_item_entrega c,
	ordem_compra_item b,
	ordem_compra a,
  ORDEM_COMPRA_VENC d		
where  a.nr_ordem_compra	= d.nr_ordem_compra
and	coalesce(a.dt_baixa::text, '') = ''
and	a.nr_ordem_compra	= b.nr_ordem_compra
and	b.nr_ordem_compra	= c.nr_ordem_compra
and	b.nr_item_oci		= c.nr_item_oci
and	a.nr_ordem_compra	= nr_ordem_compra_p
and   c.DT_CANCELAMENTO is  null
and	b.nr_seq_conta_financ	= cd_conta_financ_p
AND	d.dt_vencimento		between dt_vencimento_p - 6 and dt_final_w
and	ie_calc_fluxo_oc_w = 'V'
and IE_ENTREGA_CANC_OC_W = 'S'
order	by 1;

c01 CURSOR FOR
SELECT	a.cd_estabelecimento,
	a.CD_CONDICAO_PAGAMENTO,
	b.vl_item_liquido, -- Edgar 08/08/2006 OS 38685
	c.dt_prevista_entrega,
	b.pr_desc_financ,
	a.pr_desc_financeiro,
	b.nr_item_oci
from	Ordem_compra_item_entrega c,
	ordem_compra_item b,
	ordem_compra a
where	a.nr_ordem_compra	= nr_ordem_compra_p
and	a.nr_ordem_compra	= b.nr_ordem_compra
and	b.nr_ordem_compra	= c.nr_ordem_compra
and	b.nr_item_oci		= c.nr_item_oci
and (ie_existe_vencimentos_w	= 'N' or ie_calc_fluxo_oc_w	= 'E')
and	b.nr_seq_conta_financ	= cd_conta_financ_p
and IE_ENTREGA_CANC_OC_W = 'N'

union

SELECT	a.cd_estabelecimento,
	a.CD_CONDICAO_PAGAMENTO,
	(c.QT_PREVISTA_ENTREGA * b.VL_UNITARIO_MATERIAL) vl_item,
	c.dt_prevista_entrega,
	b.pr_desc_financ,
	a.pr_desc_financeiro,
	b.nr_item_oci
from	Ordem_compra_item_entrega c,
	ordem_compra_item b,
	ordem_compra a
where	a.nr_ordem_compra	= nr_ordem_compra_p
and	a.nr_ordem_compra	= b.nr_ordem_compra
and	b.nr_ordem_compra	= c.nr_ordem_compra
and	b.nr_item_oci		= c.nr_item_oci
and   c.DT_CANCELAMENTO is  null
and (ie_existe_vencimentos_w	= 'N' or ie_calc_fluxo_oc_w	= 'E')
and	b.nr_seq_conta_financ	= cd_conta_financ_p
and IE_ENTREGA_CANC_OC_W = 'S';


BEGIN

dt_final_w		:= coalesce(dt_final_p, dt_vencimento_p);

select	coalesce(obter_valor_liquido_ordem(nr_ordem_compra),0),
	cd_estabelecimento
into STRICT	vl_total_ordem_w,
	cd_estabelecimento_w
from	ordem_compra
where	nr_ordem_compra = nr_ordem_compra_p;

select	coalesce(ie_calc_fluxo_oc, 'V'),
		coalesce(IE_ENTREGA_CANC_OC, 'N')
into STRICT	ie_calc_fluxo_oc_w,
		IE_ENTREGA_CANC_OC_W
from	parametro_fluxo_caixa
where	cd_estabelecimento	= cd_estabelecimento_w;

vl_vencimento_w		:= 0;
vl_retorno_w		:= 0;
vl_saldo_mercadoria_w	:= 0;

ie_existe_vencimentos_w		:= 'N';

open c02;
loop
fetch c02 into
	dt_referencia_w,
	vl_fluxo_w,
	pr_desc_financ_w,
	pr_desc_financ_ordem_w,
	nr_item_oci_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	dt_orig_referencia_w := dt_referencia_w;
	if (coalesce(pr_desc_financ_w,0) > 0) then
		vl_fluxo_w		:= vl_fluxo_w - ((pr_desc_financ_w / 100) * vl_fluxo_w);
	end if;

	if (coalesce(pr_desc_financ_ordem_w,0) > 0) then
		vl_fluxo_w		:= vl_fluxo_w - ((pr_desc_financ_ordem_w / 100) * vl_fluxo_w);
	end if;

	ie_existe_vencimentos_w	:= 'S';

	if (ie_tratar_fim_semana_p = 'S') then
		dt_referencia_w			:= obter_proximo_dia_util(cd_estabelecimento_w, dt_referencia_w);
	end if;

	if (trunc(dt_referencia_w, 'dd') between trunc(dt_vencimento_p, 'dd') and trunc(dt_final_w, 'dd')) then
		vl_retorno_w		:= vl_retorno_w + vl_fluxo_w;
	end if;

end loop;
close c02;


open c01;
loop
fetch c01 into
	cd_estabelecimento_w,
	CD_CONDICAO_PAGAMENTO_w,
	vl_item_liquido_w,
	dt_entrega_w,
	pr_desc_financ_w,
	pr_desc_financ_ordem_w,
	nr_item_oci_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	SELECT * FROM Calcular_Vencimento(
		cd_estabelecimento_w, CD_CONDICAO_PAGAMENTO_w, dt_entrega_w, qt_vencimento_w, ds_vencimento_w) INTO STRICT qt_vencimento_w, ds_vencimento_w;

	vl_fluxo_w	:= dividir_sem_round(vl_item_liquido_w, qt_vencimento_w);

	if (coalesce(pr_desc_financ_w,0) > 0) then
		vl_fluxo_w		:= vl_fluxo_w - ((pr_desc_financ_w / 100) * vl_fluxo_w);
	end if;

	if (coalesce(pr_desc_financ_ordem_w,0) > 0) then
		vl_fluxo_w		:= vl_fluxo_w - ((pr_desc_financ_ordem_w / 100) * vl_fluxo_w);
	end if;

	WHILE(length(ds_vencimento_w) > 3) LOOP

		dt_referencia_w	:= To_Date(substr(ds_vencimento_w,1,10),'dd/mm/yyyy');
		ds_vencimento_w	:= substr(ds_vencimento_w, 12, length(ds_vencimento_w));

		if (ie_tratar_fim_semana_p = 'S') then
			dt_referencia_w			:= obter_proximo_dia_util(cd_estabelecimento_w, dt_referencia_w);
		end if;

		if (trunc(dt_referencia_w, 'dd') between trunc(dt_vencimento_p, 'dd') and trunc(dt_final_w, 'dd')) then
			vl_retorno_w		:= vl_retorno_w + vl_fluxo_w;
		end if;

	END LOOP;

end loop;
close c01;

if (ie_oc_parcial_p = 'S') then

	select	coalesce(sum(vl_vencimento),0)
	into STRICT	vl_venc_ant_w
	from	ordem_compra_venc
	where	nr_ordem_compra	= nr_ordem_compra_p
	and	dt_vencimento	< dt_orig_referencia_w;

	select	coalesce(sum(a.vl_mercadoria),0)
	into STRICT	vl_saldo_mercadoria_w
	from	nota_fiscal a
	where	a.nr_ordem_compra	= nr_ordem_compra_p
	and	exists (SELECT	1 from titulo_pagar x where x.nr_seq_nota_fiscal = a.nr_sequencia);

	if (vl_venc_ant_w >= vl_saldo_mercadoria_w) then
		vl_saldo_mercadoria_w	:= 0;
	else
		vl_saldo_mercadoria_w	:= vl_saldo_mercadoria_w - vl_venc_ant_w;
	end if;

	if (vl_retorno_w >= vl_saldo_mercadoria_w) then
		vl_retorno_w		:= vl_retorno_w - vl_saldo_mercadoria_w;
	else
		vl_retorno_w		:= 0;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_valor_venc_oc (nr_ordem_compra_p bigint, dt_vencimento_p timestamp, cd_conta_financ_p bigint, ie_tratar_fim_semana_p text, dt_final_p timestamp, ie_oc_parcial_p text) FROM PUBLIC;

