CREATE OR REPLACE FUNCTION obter_valor_vinculado_ret (nr_seq_retorno_p bigint, nr_seq_receb_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision;


BEGIN

if (coalesce(nr_seq_retorno_p,0) > 0) then

	select	sum(vl_vinculacao)
	into STRICT	vl_retorno_w
	from	convenio_ret_receb
	where	nr_seq_retorno	= nr_seq_retorno_p;

elsif (coalesce(nr_seq_receb_p,0) > 0) then

	select	sum(vl_vinculacao)
	into STRICT	vl_retorno_w
	from	convenio_ret_receb
	where	nr_seq_receb	= nr_seq_receb_p;

end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
