CREATE OR REPLACE FUNCTION obter_valor_w_pacote ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_pacote_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

 
 
 
/* ie_opcao_p 
 
	'T' - Total Geral 
	'P' - Total Pacote 
	'I' - Total dos Itens do Pacote 
 
*/
 
 
vl_retorno_w	double precision;

 

BEGIN 
 
if (ie_opcao_p	in ('T','P')) then 
 
	select	sum(vl_pacote) 
	into STRICT	vl_retorno_w 
	from	w_gestao_pacote 
	where (cd_procedimento	= cd_procedimento_p or ie_opcao_p = 'T') 
	and (ie_origem_procred	= ie_origem_proced_p or ie_opcao_p = 'T');
 
elsif (ie_opcao_p	= 'I') then 
	 
	select	sum(vl_item) 
	into STRICT	vl_retorno_w 
	from	conta_paciente_v 
	where	NR_SEQ_PROC_PACOTE	= nr_seq_proc_pacote_p 
	and	NR_SEQUENCIA		<> nr_seq_proc_pacote_p 
	and	cd_item			<> cd_procedimento_p 
	and	coalesce(ie_origem_proced,0) <> ie_origem_proced_p	 
	and	coalesce(cd_motivo_exc_conta::text, '') = '';
 
end if;
 
return vl_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
