CREATE OR REPLACE FUNCTION obter_variacao_entre_valor ( vl_anterior_p bigint, vl_atual_p bigint) RETURNS bigint AS $body$
DECLARE


pr_retorno_w			double precision;


BEGIN

if (vl_anterior_p IS NOT NULL AND vl_anterior_p::text <> '') and (vl_atual_p IS NOT NULL AND vl_atual_p::text <> '') then
	begin
	pr_retorno_w		:= coalesce(((dividir(vl_atual_p,vl_anterior_p)-1)*100),0);
	end;
end if;

return	round((pr_retorno_w)::numeric,4);
end;
$body$
