CREATE OR REPLACE FUNCTION obter_variacao_fiscal_item ( nr_seq_nota_p text, ie_tributo_p text, nr_item_nf_p bigint, ie_tipo_valor_fiscal_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(20);


BEGIN

select  coalesce(a.vl_tributo,0)
into STRICT	ds_retorno_w
from    nota_fiscal_item_trib a,
        nota_fiscal_item i,
        material m,
        fis_variacao_fiscal f,
        tributo b
where   i.nr_item_nf     		= a.nr_item_nf
and     i.nr_sequencia   		= a.nr_sequencia
and     m.cd_material    		= i.cd_material
and     m.cd_material    		= f.cd_material
and     a.cd_tributo 			= b.cd_tributo
and	f.ie_situacao			= 'A'
and     i.nr_sequencia   		= nr_seq_nota_p
and	i.nr_item_nf			= nr_item_nf_p
and     b.ie_tipo_tributo    		= ie_tributo_p
and     f.cd_codigo_valores_fiscais	= ie_tipo_valor_fiscal_p;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
