CREATE OR REPLACE FUNCTION obter_variacao_peso_pac (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_retorno_w	varchar(255);
dt_registro_w	timestamp;


BEGIN

select	max(nr_sequencia),
	max(dt_sinal_vital)
into STRICT	nr_sequencia_w,
	dt_registro_w
from	atendimento_sinal_vital
where	cd_paciente = cd_pessoa_fisica_p
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and		ie_situacao = 'A';

if (coalesce(nr_sequencia_w,0) > 0) then
	if	((dt_registro_w+1/24) >= clock_timestamp()) then
		select	substr(obter_alerta_sinal_vital(nr_sequencia_w),1,255)
		into STRICT	ds_retorno_w
		;
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
