CREATE OR REPLACE FUNCTION obter_vel_infusao_npt ( nr_prescricao_p bigint, nr_seq_item_p bigint) RETURNS bigint AS $body$
DECLARE


qt_vel_infusao_w	double precision;


BEGIN

select	max(a.qt_dosagem)
into STRICT	qt_vel_infusao_w
from	prescr_solucao_evento a
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_seq_nut_neo = nr_seq_item_p
and	a.dt_atualizacao = (	SELECT	max(b.dt_atualizacao)
				from	prescr_solucao_evento b
				where	b.nr_prescricao = a.nr_prescricao
				and	a.nr_seq_nut_neo = b.nr_seq_nut_neo
				and	ie_alteracao in (1,3,5));

return	qt_vel_infusao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
