CREATE OR REPLACE FUNCTION obter_vencimento_dia_semana ( dt_referencia_p timestamp, ie_dia_semana_p text, cd_estabelecimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp;
ie_dia_semana_w			varchar(2);
dt_referencia_w			timestamp;
ie_dia_util_w			varchar(2);


BEGIN

dt_referencia_w	:= dt_referencia_p;

select	pkg_date_utils.get_WeekDay(dt_referencia_p),
	obter_se_dia_util(dt_referencia_p,cd_estabelecimento_p)
into STRICT	ie_dia_semana_w,
	ie_dia_util_w
;

if (ie_dia_semana_w = ie_dia_semana_p) and (ie_dia_util_w = 'S') then
	dt_retorno_w	:= dt_referencia_p;
else
	while(ie_dia_semana_w <> ie_dia_semana_p) or (ie_dia_util_w = 'N') loop
		begin
		select (dt_referencia_w + 1),
			pkg_date_utils.get_WeekDay(dt_referencia_w + 1),
			obter_se_dia_util(dt_referencia_w + 1,cd_estabelecimento_p)
		into STRICT	dt_referencia_w,
			ie_dia_semana_w,
			ie_dia_util_w
		;
		end;
	end loop;

	dt_retorno_w	:= dt_referencia_w;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
