CREATE OR REPLACE FUNCTION obter_vencimento_nf_protocolo (nr_seq_protocolo_p bigint) RETURNS timestamp AS $body$
DECLARE

dt_vencimento_w		timestamp;


BEGIN

if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	begin
	
	select	min(b.dt_vencimento)
	into STRICT	dt_vencimento_w
	from	nota_fiscal_venc b,
		nota_fiscal a
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p
	and	a.nr_sequencia	= b.nr_sequencia;
	
	end;
end if;

return dt_vencimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
