CREATE OR REPLACE FUNCTION obter_venc_titulo_data ( nr_titulo_p bigint, dt_contabil_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
dt_alteracao_w		timestamp;
dt_anterior_w		timestamp;
dt_vencimento_w		timestamp;

c01 CURSOR FOR
SELECT	dt_alteracao,
	dt_anterior,
	dt_vencimento
from	alteracao_vencimento
where	nr_titulo		= nr_titulo_p
order 	by dt_alteracao,
	nr_sequencia;


BEGIN

dt_retorno_w	:= null;

open c01;
loop
fetch c01 into
	dt_alteracao_w,
	dt_anterior_w,
	dt_vencimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (coalesce(dt_retorno_w::text, '') = '') then
		dt_retorno_w	:= dt_anterior_w;
	end if;
	if (trunc(dt_alteracao_w, 'dd') <= coalesce(dt_contabil_p, clock_timestamp())) then
		dt_retorno_w	:= dt_vencimento_w;
	end if;
end loop;
close c01;

if (coalesce(dt_retorno_w::text, '') = '') then
	select	max(coalesce(dt_pagamento_previsto, dt_vencimento))
	into STRICT	dt_retorno_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_venc_titulo_data ( nr_titulo_p bigint, dt_contabil_p timestamp) FROM PUBLIC;

