CREATE OR REPLACE FUNCTION obter_verificador_antibiotico (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possue_w 	varchar(1);
qt_total_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_total_w
from   	prescr_medica pme,
		prescr_material pma,
		material m
where  	pme.nr_atendimento 		= nr_atendimento_p
and     pme.nr_prescricao 		= pma.nr_prescricao
and		obter_se_mat_antibiotico(m.cd_material) = 'S'
and    	pma.cd_material 		= m.cd_material;



if (coalesce(qt_total_w,0) >= 1) then
	ie_possue_w := 'S';
else
	ie_possue_w := 'N';
end if;

return	ie_possue_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
