SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_versao_carta_medica ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

				
ds_retorno_w			      varchar(100);		
nr_versao_w				      carta_medica.nr_versao%TYPE;
qt_versao_w             carta_medica.nr_versao%TYPE;
nr_seq_carta_mae_w      carta_medica.nr_seq_carta_mae%TYPE;

BEGIN

select max(nr_seq_carta_mae)
into STRICT   nr_seq_carta_mae_w
from   carta_medica
where  nr_sequencia = nr_sequencia_p;

select	coalesce(count(nr_versao),0)
into STRICT	  qt_versao_w
from   	carta_medica a
where	nr_seq_carta_mae	= nr_seq_carta_mae_w
and		ie_preliminar = 'S'
and		nr_sequencia <= nr_sequencia_p;

ds_retorno_w := qt_versao_w;

select	coalesce(count(nr_versao),0)
into STRICT	nr_versao_w
from   	carta_medica a
where	nr_seq_carta_mae = nr_seq_carta_mae_w
and		ie_preliminar in ('N', 'A')
and		nr_sequencia <= nr_sequencia_p;


if (pkg_i18n.get_user_locale <>  'de_AT') then
    if (nr_versao_w > 0) then
      ds_retorno_w := qt_versao_w ||'.'||nr_versao_w;
    end if;
else
    ds_retorno_w := qt_versao_w + nr_versao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
