SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_versao_ciap ( cd_ciap_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_versao_w CAPITULO_CIAP.NR_SEQ_VERSAO%type;

BEGIN

if (cd_ciap_p IS NOT NULL AND cd_ciap_p::text <> '') then

	select max(c.nr_seq_versao)
	into STRICT nr_seq_versao_w
	from PROBLEMA_CIAP a,
		COMPONENTE_CIAP b,
		CAPITULO_CIAP c
	where a.nr_seq_Componente = b.nr_sequencia
		and b.nr_seq_capitulo = c.nr_sequencia
		and a.cd_ciap = cd_ciap_p
		and (nr_seq_versao IS NOT NULL AND nr_seq_versao::text <> '');

	if (nr_seq_versao_w IS NOT NULL AND nr_seq_versao_w::text <> '') then
		select 	SUBSTR(ie_localizacao||' - '||obter_desc_expressao(301639)||': '||nr_versao||' - '||
      obter_desc_expressao(297891)||': '||to_char(dt_revisao, 'dd/mm/yyyy'),1, 255)
		into STRICT ds_retorno_w
		from	ciap_versao_philips
		where nr_sequencia = nr_seq_versao_w;
	end if;
end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
