CREATE OR REPLACE FUNCTION obter_versao_defeito (dt_ocorrencia_defeito_p timestamp) RETURNS varchar AS $body$
DECLARE



cd_versao_w		varchar(15);


BEGIN

select  max(x.cd_versao)
into STRICT	cd_versao_w
from    aplicacao_tasy_versao x
where   x.dt_versao =
	(SELECT min(a.dt_versao)
		from  	aplicacao_tasy_versao a
	   	where 	a.cd_aplicacao_tasy 		= 'Tasy'
		and   	dt_ocorrencia_defeito_p 	<= a.dt_versao);

return	cd_versao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
