SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_versao_simpro ( cd_material_p bigint, cd_convenio_p bigint, cd_estabelecimento_p bigint, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


vl_preco_fabrica_w	double precision;
vl_preco_venda_w	double precision;
vl_preco_w		double precision;
cd_simpro_w		bigint;
qt_conversao_w		double precision;
ie_tipo_preco_w		varchar(01);
ie_tipo_convenio_w	smallint;
cd_versao_simpro_w	varchar(20);
cd_estab_simpro_w	estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	coalesce(max(qt_conversao),1),
		coalesce(max(cd_simpro),0)
	from	material_simpro
	where	cd_material	= cd_material_p
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p, 0))	= coalesce(cd_estabelecimento_p, 0)
	and	coalesce(cd_convenio, coalesce(cd_convenio_p,0)) 		= coalesce(cd_convenio_p,0)
	and	coalesce(ie_tipo_convenio, coalesce(ie_tipo_convenio_w,0))	= coalesce(ie_tipo_convenio_w,0)
	and 	dt_vigencia_p between coalesce(dt_vigencia, dt_vigencia_p) and coalesce(dt_final_vigencia, dt_vigencia_p)
	and	coalesce(ie_situacao,'A')					= 'A'
	order by
		coalesce(cd_estabelecimento, 0),
		coalesce(cd_convenio,0),
		coalesce(ie_tipo_convenio,0),
		coalesce(dt_vigencia, dt_vigencia_p - 5000);

BEGIN

cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;

select 	max(ie_tipo_convenio)
into STRICT	ie_tipo_convenio_w
from 	convenio
where 	cd_convenio = cd_convenio_p;

open C01;
loop
fetch C01 into
	qt_conversao_w,
	cd_simpro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_conversao_w	:= qt_conversao_w;
	cd_simpro_w	:= cd_simpro_w;
	end;
end loop;
close C01;


vl_preco_w:= 0;

if (cd_simpro_w > 0) then
	select 	max(cd_versao_simpro)
	into STRICT	cd_versao_simpro_w
	from	simpro_preco
	where	cd_simpro = cd_simpro_w
	and	coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
	and	dt_vigencia = (
				SELECT max(dt_vigencia)
				from 	simpro_preco
				where 	cd_simpro = cd_simpro_w
				and	coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0)) = coalesce(cd_estab_simpro_w, 0)
				and 	dt_vigencia <= dt_vigencia_p);

end if;

return cd_versao_simpro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_versao_simpro ( cd_material_p bigint, cd_convenio_p bigint, cd_estabelecimento_p bigint, dt_vigencia_p timestamp) FROM PUBLIC;

